import{_ as t,o as e,c as d,O as r}from"./chunks/framework.51846e02.js";const _=JSON.parse('{"title":"网络","description":"","frontmatter":{},"headers":[],"relativePath":"develop/Interview/Offer/network.md","filePath":"develop/Interview/Offer/network.md"}'),a={name:"develop/Interview/Offer/network.md"},o=r('<h1 id="网络" tabindex="-1">网络 <a class="header-anchor" href="#网络" aria-label="Permalink to &quot;网络&quot;">​</a></h1><h2 id="http-和-https-的区别" tabindex="-1">HTTP 和 HTTPS 的区别 <a class="header-anchor" href="#http-和-https-的区别" aria-label="Permalink to &quot;HTTP 和 HTTPS 的区别&quot;">​</a></h2><table><thead><tr><th>区别</th><th>HTTP</th><th>HTTPS</th></tr></thead><tbody><tr><td>URL</td><td><code>http://</code></td><td><code>https://</code></td></tr><tr><td>端口</td><td>80</td><td>443</td></tr><tr><td>安全</td><td>不安全</td><td>安全</td></tr><tr><td>OSI 网络模型</td><td>应用层</td><td>安全传输机制工作在传输层</td></tr><tr><td>加密</td><td>无法加密</td><td>对传输的数据加密</td></tr><tr><td>证书</td><td>无需证书</td><td>CA 机构颁发的 SSL 证书</td></tr></tbody></table><h3 id="为什么-https-是安全的" tabindex="-1">为什么 HTTPS 是安全的？ <a class="header-anchor" href="#为什么-https-是安全的" aria-label="Permalink to &quot;为什么 HTTPS 是安全的？&quot;">​</a></h3><p>HTTPS 在 TCP 和 HTTP 网络层之间加入了 SSL/TLS 安全协议，使得报文能够加密传输。</p><p>HTTP 的建立在 TCP 三次握手后可以进行 HTTP 报文传输；而 HTTPS 在 TCP 三次握手后，还需要 SSL/TLS 的握手，才能进行加密报文传输。</p><h2 id="xss、csrf" tabindex="-1">XSS、CSRF <a class="header-anchor" href="#xss、csrf" aria-label="Permalink to &quot;XSS、CSRF&quot;">​</a></h2><ul><li><p>XSS</p><p>XSS（Cross-Site Scripting，跨站脚本攻击）：它会在 WEB 页面中插入恶意脚本，当用户浏览该页面时，执行这些代码，从而盗取用户信息（Cookie，token）、破坏页面结构、重定向到其他网站。</p><p>XSS 攻击的三种类型：存储型、DOM 型、反射型</p><p>预防方式：尽量不使用 <code>innerHtml</code> 插入 HTML 内容；url 参数使用 <code>encodeURIComponent</code> 方法转义</p></li><li><p>CSRF</p><p>CSRF（Cross-site request forgery，跨站请求伪造）：攻击者诱导受害者进入第三方网站，在第三方网站中，向被攻击网站发送跨站请求。利用受害者在被攻击网站已经获取的注册凭证，绕过后台的用户验证，达到冒充用户对被攻击的网站执行某项操作的目的。</p><p>预防方式：添加验证码；使用 token；Referer 识别</p></li></ul><h2 id="强制缓存、协商缓存" tabindex="-1">强制缓存、协商缓存 <a class="header-anchor" href="#强制缓存、协商缓存" aria-label="Permalink to &quot;强制缓存、协商缓存&quot;">​</a></h2>',9),n=[o];function s(h,i,l,p,c,T){return e(),d("div",null,n)}const P=t(a,[["render",s]]);export{_ as __pageData,P as default};
